      if (data.articles) {
        defaultArticleInfo = {
          pageCount: data.pageAll,
          currentPage: page,
          articles: data.articles,
        };
      }
    });
  return {
    props: {
      defaultArticleInfo,
    },
  };
}
const mapStateToProps = (state) => {
  return {
    user: state.user,
  };
};
export default connect(mapStateToProps)(Main);
