                                if (itemY[0] !== "typeID")
                                  return (
                                    <>
                                      {item !== 4 ||
                                        (itemY[0] != "length" &&
                                          itemY[0] != "width" &&
                                          itemY[0] != "height" && (
                                            <div key={index}>
                                              <span
                                                dangerouslySetInnerHTML={{
                                                  __html: CargoTypeList.find(
                                                    (itemX) => itemX.id === item
